// Parse OpenAI response and format for our application
const openaiResponse = $input.first().json.choices[0].text;

try {
  // Extract title and content from the formatted response
  const titleMatch = openaiResponse.match(/TITRE:\s*(.+)/i);
  const contentMatch = openaiResponse.match(/CONTENU:\s*([\s\S]+)/i);

  let title = '';
  let content = '';

  if (titleMatch && titleMatch[1]) {
    title = titleMatch[1].trim();
    // Ensure title is not longer than 60 characters
    if (title.length > 60) {
      title = title.substring(0, 57) + '...';
    }
  } else {
    // Fallback: extract first line as title
    const lines = openaiResponse.split('\n').filter(line => line.trim());
    title = lines[0] ? lines[0].trim().substring(0, 60) : 'Article généré par IA';
  }

  if (contentMatch && contentMatch[1]) {
    content = contentMatch[1].trim();
  } else {
    // Fallback: use entire response as content
    content = openaiResponse.trim();
  }

  // Clean up content
  content = content.replace(/^TITRE:\s*.+$/gim, '').trim();

  // Calculate stats
  const wordCount = content.split(/\s+/).filter(word => word.length > 0).length;
  const characterCount = content.length;

  return {
    success: true,
    title: title,
    content: content,
    error: null,
    timestamp: new Date().toISOString(),
    wordCount: wordCount,
    characterCount: characterCount
  };

} catch (error) {
  return {
    success: false,
    title: '',
    content: '',
    error: 'Erreur lors du traitement de la réponse IA: ' + error.message,
    timestamp: new Date().toISOString()
  };
}

// Return error response for invalid input
return {
  success: false,
  title: '',
  content: '',
  error: 'Le prompt est requis et ne peut pas être vide',
  timestamp: new Date().toISOString()
};

// Handle OpenAI API errors
const error = $input.first().json.error || $input.first().json;
let errorMessage = 'Erreur lors de la génération de l\'article';

if (error.message) {
  errorMessage = error.message;
} else if (typeof error === 'string') {
  errorMessage = error;
}

return {
  success: false,
  title: '',
  content: '',
  error: errorMessage,
  timestamp: new Date().toISOString()
};
